INSERT INTO `summary_sqld` VALUES (1,'2025-03-14 03:29:22.318000','{\"topic\": \"데이터모델의 이해\", \"subject\": \"데이터 모델링의 이해\", \"version\": \"1.0\", \"lastModified\": \"2025-03-14T03:29:22.318Z\"}','[{\"id\": \"1741922807160-vanryjjcz\", \"name\": \"시트 1\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"title-section-1741922807160\", \"type\": \"layout-area\", \"label\": \"표지\", \"style\": \"title\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"표지\", \"title\": \"데이터 모델링의 이해\", \"author\": \"정우균\", \"subtitle\": \"개론\"}}]}, {\"id\": \"1741922807623-jylb073cb\", \"name\": \"시트 2\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"toc-section-1741922807623\", \"type\": \"layout-area\", \"label\": \"CONTENTS\", \"style\": \"toc\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"목차\", \"items\": [{\"id\": \"1\", \"text\": \"모델링의 개념\", \"number\": \"01\"}, {\"id\": \"2\", \"text\": \"모델링의 특징\", \"number\": \"02\"}, {\"id\": \"3\", \"text\": \"데이터 모델링 3가지 관점\", \"number\": \"03\"}, {\"id\": \"4\", \"text\": \"데이터 모델링 유의점\", \"number\": \"04\"}, {\"id\": \"1741922842820\", \"text\": \"데이터 모델링 3가지 요소\", \"number\": \"05\"}, {\"id\": \"1741922843900\", \"text\": \"데이터 모델링 3단계\", \"number\": \"06\"}, {\"id\": \"1741922844613\", \"text\": \"스키마의 3단계 구조\", \"number\": \"07\"}, {\"id\": \"1741922845267\", \"text\": \"데이터 모델의 표기법(ERD : Entity Relationship Diagram)\", \"number\": \"08\"}, {\"id\": \"1741922845888\", \"text\": \"ERD 작성 절차 (6단계) \", \"number\": \"09\"}]}}]}, {\"id\": \"1741922807911-5nuc24a1m\", \"name\": \"시트 3\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"content-section-1741922807911\", \"type\": \"layout-area\", \"label\": \"TITLE\", \"style\": \"content\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"내용\", \"items\": [{\"id\": \"1741922807911\", \"word\": \"모델링의 개념\", \"images\": [], \"showImages\": true, \"description\": \"현실 세계의 비즈니스 프로세스와 데이터 요구 사항을 추상적이고 구조화된 형태로 표현\\n데이터베이스 구조와 관계를 정의하며 저장, 조작, 관리 방법 명확하게 정의\"}, {\"id\": \"1741922868961\", \"word\": \"모델링의 특징\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"단순화(Simplification)\\n- 현실을 단순화하여 핵심 요소에 집중하고 불필요한 세부 사항을 제거\\n- 단순화를 통해 복잡한 현실 세계를 이해하고 표현하기 쉬워짐 \\n\\n추상화(Abstraction)\\n- 현실세계를 일정한 형식에 맞추어 간략하게 대략적으로 표현하는 과정\\n- 다양한 현상을 일정한 양식인 표기법에 따라 표현\\n\\n명확화(Clarity)\\n- 대상에 대한 애매모호함을 최대한 제거하고 정확하게 현상을 기술하는 과정\\n- 명확화를 통해 모델을 이해하는 이들의 의사소통을 원활히 함\", \"showDescription\": true}, {\"id\": \"1741922870149\", \"word\": \"데이터 모델링 3가지 관점\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"데이터 관점\\n    - 데이터가 어떻게 저장되고, 접근되고, 관리되는지를 정의하는 단계\\n프로세스 관점\\n    - 시스템이 어떤 작업을 수행하며, 이러한 작업들이 어떻게 조직되고 조정되는지를 정의하는 단계 \\n    - 데이터가 시스템 내에서 어떻게 흐르고 변환되는지에 대한 확인 \\n데이터와 프로세스 관점\\n    - 데이터 관점과 프로세스 관점을 결합하여 시스템의 전반적인 동작을 이해하는 단계 \\n    - 특정 프로세스가 어떤 데이터를 사용하는지, 데이터가 어떻게 생성되고 변경되는지를 명확하게 정의\", \"showDescription\": true}, {\"id\": \"1741922871405\", \"word\": \"데이터 모델링 유의점\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"중복(Duplication)\\n    - 한 테이블 또는 여러 테이블에 같은 정보를 저장하지 않도록 설계\\n비유연성(Inflexibility)\\n    - 사소한 업무 변화에 대해서도 잦은 모델 변경이 되지 않도록 주의\\n    - 데이터 정의를 프로세스와 분리\\n비일관성(Inconsistency)\\n    - 데이터베이스 내의 정보가 모순되거나 상반된 내용을 갖는 상태를 의미 \\n    - 데이터간 상호연관 관계를 명확히 정의 \\n    - 데이터 품질 관리 필요 \\n    - 데이터의 중복이 없더라도 비일관성은 발생할 수 있음\", \"showDescription\": true}, {\"id\": \"1741922872541\", \"word\": \"데이터 모델링 3가지 요소\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"- 대상(Entity) : 업무가 관리하고자 하는 대상(객체) \\n- 속성(Attribute) : 대상들이 갖는 속성(하나의 특징으로 정의될 수 있는 것) \\n- 관계(Relationship) : 대상들 간의 관계 \\n\", \"showDescription\": true}, {\"id\": \"1741922873410\", \"word\": \"데이터 모델링 3단계\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"개념적 모델링\\n    - 업무 중심적이고 포괄적(전사적)인 수준의 모델링 \\n    - \\\"추상화 수준이 가장 높음\\\"  \\n    - 업무를 분석 뒤 업무의 핵심 엔터티(Entity)를 추출하는 단계 \\n    - 도출된 핵심 엔터티(Entity)들과의 관계들을 표현하기 위해 ERD 작성\\n\\n논리적 모델링\\n    - 개념적 모델링의 결과를 토대로 세부속성, 식별자, 관계 등을 표현하는 단계 \\n    - 데이터 구조를 정의하기 때문에 비슷한 업무나 프로젝트에서 동일한 형태의 데이터 사용 시 재사용 가능 \\n    - 동일한 논리적 모델을 사용하는 경우 쿼리도 재사용 가능 \\n    - 데이터 정규화 수행 \\n    - 재사용성이 높은 논리적 모델은 유지보수가 용이해짐 \\n\\n물리적 모델링\\n    - 논리 모델링이 끝나면 이를 직접 물리적으로 생성하는 과정 \\n    - 데이터베이스 성능, 디스크 저장구조, 하드웨어의 보안성, 가용성 등을 고려 \\n    - 가장 구체적인 데이터 모델링 \\n    - \\\"추상화 수준은 가장 낮음(가장 구체적인 모델링이므로) \\\"\", \"showDescription\": true}, {\"id\": \"1741922874091\", \"word\": \"스키마의 3단계 구조\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"스키마 : 데이터베이스의 구조와 제약 조건에 관한 전반적인 명세를 기술한 메타데이터의 집합 \\n- 외부, 개념, 내부 스키마로 분리 \\n- 사용자의 관점과 실제 설계된 물리적인 방식을 분리하기 위해 고안됨 \\n\\n외부 스키마\\n    - 사용자가 보는 관점에서 데이터베이스 스키마를 정의 \\n    - 사용자나 응용 프로그램이 필요한 데이터를 정의(View : 사용자가 접근하는 대상) \\n\\n개념 스키마\\n    - 사용자 관점의 데이터베이스 스키마를 통합하여 데이터베이스의 전체 논리적 구조를 정의 \\n    - 전체 데이터베이스의 개체, 속성, 관계, 데이터 타입 등을 정의 \\n\\n내부 스키마\\n    - 데이터가 물리적으로 어떻게 저장되는지를 정의 \\n    - 데이터의 저장 구조, 컬럼, 인덱스 등을 정의함 \\n\\n** 3단계 스키마의 독립성 \\n- 독립성 : 물리적, 논리적 구조를 변경하더라도 사용자가 사용하는 응용 프로그램에 영향을 주지 말아야 함 \\n1) 논리적 독립성 : 논리적 데이터 구조가 변경되어도(개념 스키마 변경) 응용 프로그램에 영향을 주지 않는 특성 \\n2) 물리적 독립성 : 물리적 구조가 변경되어도(내부 스키마 변경) 개념/외부 스키마에 영향을 주지 않는 특성\", \"showDescription\": true}, {\"id\": \"1741922874762\", \"word\": \"데이터 모델의 표기법(ERD : Entity Relationship Diagram)\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \" - 엔터티(Entity)와 엔터티 간의 관계(Relationship)를 시각적으로 표현한 다이어그램 \\n - 1976 년 피터 첸(Peter Chen)이 만든 표기법, 데이터 모델링 표준으로 사용\", \"showDescription\": true}, {\"id\": \"1741922954676\", \"word\": \"ERD 작성 절차 (6단계) \", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"① 엔터티를 도출한 후 그린다 \\n② 엔터티 배치 \\n③ 엔터티 간의 관계를 설정 \\n④ 관계명을 서술 \\n⑤ 관계의 참여도 기술 \\n⑥ 관계의 필수 여부를 확인\", \"showDescription\": true}]}}]}]','\"데이터 모델링의 이해\"','\"데이터모델의 이해\"',NULL,NULL),(2,'2025-03-14 03:54:48.385000','{\"topic\": \"엔터티\", \"subject\": \"데이터 모델링의 이해\", \"version\": \"1.0\", \"lastModified\": \"2025-03-14T03:54:48.385Z\"}','[{\"id\": \"1741924383874-117kbghq3\", \"name\": \"시트 1\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"title-section-1741924383874\", \"type\": \"layout-area\", \"label\": \"표지\", \"style\": \"title\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"표지\", \"title\": \"데이터 모델링의 이해\", \"author\": \"정우균\", \"subtitle\": \"엔터티\"}}]}, {\"id\": \"1741924384107-k0vg31klg\", \"name\": \"시트 2\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"toc-section-1741924384107\", \"type\": \"layout-area\", \"label\": \"CONTENTS\", \"style\": \"toc\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"목차\", \"items\": [{\"id\": \"1\", \"text\": \"엔터티의 개념 \", \"number\": \"01\"}, {\"id\": \"2\", \"text\": \"엔터티의 특징\", \"number\": \"02\"}, {\"id\": \"3\", \"text\": \"엔터티의 분류\", \"number\": \"03\"}, {\"id\": \"4\", \"text\": \"엔터티의 명명 \", \"number\": \"04\"}, {\"id\": \"1741924416181\", \"text\": \"엔터티와 인스턴스 표기법\", \"number\": \"05\"}]}}]}, {\"id\": \"1741924384338-holgirzk5\", \"name\": \"시트 3\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"content-section-1741924384338\", \"type\": \"layout-area\", \"label\": \"TITLE\", \"style\": \"content\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"내용\", \"items\": [{\"id\": \"1741924384338\", \"word\": \"엔터티의 개념 \", \"images\": [], \"showImages\": true, \"description\": \"- 현실 세계에서 독립적으로 식별 가능한 객체나 사물을 나타냄 \\n- 엔터티는 업무상 분석해야 하는 대상(Instance)들로 이루어진 집합 \\n- 인스턴스는 엔터티의 특정한 속성 값들로 구성되며, 엔터티의 개념을 현실에서 구체적으로 나타낸 것 \\n예) 엔터티와 속성, 인스턴스 등의 관계 \\n\\n엔터티(Entity) : 학생 \\n속성(Attribute) : 학번, 이름, 학과 등. \\n식별자(Identifier) : 학번 (고유한 학번으로 각 학생을 식별) \\n인스턴스 : 특정 학생의 데이터  - 학번: 2021001 - 이름: 홍길동  - 학과: 컴퓨터 공학 \\n\"}, {\"id\": \"1741924439348\", \"word\": \"엔터티의 특징\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"1. 유일한 식별자에 의해 식별 가능 \\n- 인스턴스가 식별자에 의해 한 개씩만 존재하는 지 검증 필요 \\n- 유일한 식별자는 그 엔터티의 인스턴스만의 고유 이름 \\nex) 이름은 동명이인이 있을 수 있으므로 사번, 학번 등이 고유식별자 \\n\\n2. 해당 업무에 필요하고 관리하고자 하는 정보 \\n- 설계하는 업무의 시스템 구축에 필요한 정보이어야 함 \\nex) 학교 시스템 구축 시 학생정보 필요. 다른 업무엔 학생 정보 불필요. \\n\\n3. 인스턴스들의 집합 \\n- 영속적으로 존재하는 2개 이상의 인스턴스의 집합 \\n- 인스턴스가 한 개 밖에 없는 엔터티는 집합이 아니므로 성립이 안됨. \\n\\n4. 엔터티는 반드시 속성을 가짐 \\n- 각 엔터티는 2개 이상의 속성을 가짐 \\n- 하나의 인스턴스는 각각의 속성들에 대한 1개의 속성 값만을 가짐 \\nex) 학생 엔터티에서 한 학생의 데이터(인스턴스)의 이름(속성) 정보에는 반드시 한 값만 저장됨 \\n\\n5. 엔터티는 업무 프로세스에 의해 이용 \\n- 업무적으로 필요해 선정했지만 실제 사용되지 않으면 잘못 설계된 것 \\n- 모델링 시 발견하기 어려운 경우 데이터 모델 검증이나 상관 모델링 시 단위 프로세스 교차점검으로 \\n\\n문제 도출 - 누락된 프로세스의 경우 추후 해당 프로세스 추가 \\n- 반대로 사용되지 않는 고립 엔터티는 제거 필요 \\n\\n6. 다른 엔터티와 최소 1개 이상의 관계 성립 \\n- 엔터티는 업무적 연관성을 갖고 다른 엔터티와 연관의 의미를 가짐 \\n- 관계가 없는 엔터티 도출은 부적절한 엔터티이거나 적절한 관계를 찾지 못한 것\\n\", \"showDescription\": true}, {\"id\": \"1741924452252\", \"word\": \"엔터티의 분류\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"1) 유형과 무형에 따른 분류 \\n① 유형엔터티 - 물리적 형태가 있음(실체가 있는 대상) - 안정적이며 지속적으로 활용되는 엔터티 - 업무로부터 구분하기가 가장 용이한 엔터티 \\nex) 사원, 물품, 강사 등 \\n② 개념엔터티 - 물리적인 형태 없음 - 관리해야 할 개념적 정보로부터 구분되는 엔터티 \\nex)조직, 보험상품 등 \\n③ 사건엔터티 - 업무를 수행에 따라 발생하는 엔터티 - 발생량이 많고 각종 통계자료에 이용 \\nex) 주문, 청구, 미납 등\\n\\n2) 발생 시점에 따른 분류 \\nHDATALAB \\n① 기본엔터티  - 그 업무에 원래 존재하는 정보 - 다른 엔터티와 관계에 의해 생성되지 않고 독립적으로 생성 - 타 엔터티의 부모 역할을 하는 엔터티 - 다른 엔터티로부터 주식별자를 상속받지 않고 자신의 고유한 주식별자를 가짐 \\nex) 사원, 부서, 고객, 상품 등 \\n② 중심엔터티 - 기본엔터티로부터 발생되고 그 업무에서 중심적인 역할 - 많은 데이터가 발생되고 다른 엔터티와의 관계를 통해 많은 행위 엔터티를 생성 \\nex) 계약, 사고, 청구, 주문, 매출 등 \\n③ 행위엔터티 - 2개 이상의 부모엔터티로부터 발생 - 자주 내용이 바뀌거나 데이터 양이 증가 - 분석 초기 단계보다는 상세 설계 단계나 프로세스와 상관모델링을 진행하면서 도출 \\nex) 주문(고객과 상품 엔터티로 부터 발생하므로 행위엔터티이기도 함), 사원변경이력, 이력 등\\n\", \"showDescription\": true}, {\"id\": \"1741924461499\", \"word\": \"엔터티의 명명 \", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"① 현업에서 사용하는 용어 사용 \\n② 가능하면 약자 사용은 자제 \\n③ 단수 명사 사용 \\n④ 모든 엔터티에서 유일하게 이름 부여 \\n⑤ 엔터티 생성 의미대로 이름 부여 \", \"showDescription\": true}, {\"id\": \"1741924469708\", \"word\": \"엔터티와 인스턴스 표기법\", \"images\": [{\"alt\": \"1 엔터티와 인스턴스 표기법.png\", \"url\": \"data:image/png;base64,\"}], \"showWord\": true, \"showImages\": true, \"description\": \"\", \"showDescription\": true}]}}]}]','\"데이터 모델링의 이해\"','\"엔터티\"',NULL,NULL),(3,'2025-03-14 04:10:42.693000','{\"topic\": \"속성\", \"subject\": \"데이터 모델링의 이해\", \"version\": \"1.0\", \"lastModified\": \"2025-03-14T04:10:42.693Z\"}','[{\"id\": \"1741925273129-tscux1u97\", \"name\": \"시트 1\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"title-section-1741925273129\", \"type\": \"layout-area\", \"label\": \"표지\", \"style\": \"title\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"표지\", \"title\": \"데이터 모델링의 이해\", \"author\": \"정우균\", \"subtitle\": \"속성\"}}]}, {\"id\": \"1741925273378-7we11pfk1\", \"name\": \"시트 2\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"toc-section-1741925273378\", \"type\": \"layout-area\", \"label\": \"CONTENTS\", \"style\": \"toc\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"목차\", \"items\": [{\"id\": \"1\", \"text\": \"속성(Attribute)의 개념 \", \"number\": \"01\"}, {\"id\": \"2\", \"text\": \"엔터티, 인스턴스, 속성, 속성값의 관계 \", \"number\": \"02\"}, {\"id\": \"3\", \"text\": \" 속성의 특징\", \"number\": \"03\"}, {\"id\": \"4\", \"text\": \"** 원자성이란 \", \"number\": \"04\"}, {\"id\": \"1741925328302\", \"text\": \" 함수적 종속성 \", \"number\": \"05\"}, {\"id\": \"1741925331644\", \"text\": \"속성의 분류\", \"number\": \"06\"}, {\"id\": \"1741925335741\", \"text\": \"속성의 명명규칙 \", \"number\": \"07\"}, {\"id\": \"1741925339737\", \"text\": \"도메인(Domain) \", \"number\": \"08\"}]}}]}, {\"id\": \"1741925273666-jqkrjug1w\", \"name\": \"시트 3\", \"content\": [{\"x\": \"0%\", \"y\": \"0%\", \"id\": \"content-section-1741925273666\", \"type\": \"layout-area\", \"label\": \"TITLE\", \"style\": \"content\", \"width\": \"98%\", \"height\": \"95%\", \"position\": {\"x\": 12, \"y\": 8.33}, \"template\": {\"type\": \"내용\", \"items\": [{\"id\": \"1741925273666\", \"word\": \"속성(Attribute)의 개념\", \"images\": [], \"showImages\": true, \"description\": \" 속성은 업무에서 필요로 하는 고유한 성질, 특징을 의미(관찰 대상) -> 컬럼으로 표현할 수 있는 단위! \\n - 업무상 인스턴스로 관리하고자 하는 더 이상 분리되지 않는 최소의 데이터 단위 - 인스턴스의 구성 요소 \\nex) 학생 엔터티에 이름, 학번, 학과번호 등이 속성이 될 수 있음\"}, {\"id\": \"1741925350993\", \"word\": \"엔터티, 인스턴스, 속성, 속성값의 관계 \", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"- 한 개의 엔터티는 2개 이상의 인스턴스의 집합이어야 한다(하나의 테이블은 두 개 이상의 행을 가짐) \\n- 한 개의 엔터티는 2개 이상의 속성을 갖는다(하나의 테이블은 두 개 이상의 컬럼으로 구성됨) \\n- 한 개의 속성은 1개의 속성값을 갖는다(각 컬럼의 값은 하나씩만 삽입 가능) \\n- 속성은 엔티티에 속한 엔티티에 대한 자세하고 구체적인 정보를 나타냄, 각 속성은 구체적인 값을 가짐\", \"showDescription\": true}, {\"id\": \"1741925352146\", \"word\": \" 속성의 특징\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"- 반드시 해당 업무에서 필요하고 관리하고자 하는 정보여야 한다 \\n- 정해진 주식별자에 함수적 종속성을 가져야 한다 \\n- 하나의 속성은 한 개의 값만을 가진다.(한 컬럼의 값은 각 인스턴스마다 하나씩만 저장) \\n- 하나의 속성에 여러 개의 값이 있는 다중값일 경우 별도의 엔터티를 이용하여 분리한다 \\n- 하나의 인스턴스는 속성마다 반드시 하나의 속성값을 가진다 \\n=> 각 속성이 하나의 값을 갖고 있음을 의미(속성의 원자성) \", \"showDescription\": true}, {\"id\": \"1741925352925\", \"word\": \"** 원자성이란 \", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"- 데이터모델에서 각 엔터티의 인스턴스가 해당 속성에 대해 단일하고 명확한 값을 가지는 것을 의미\", \"showDescription\": true}, {\"id\": \"1741925353655\", \"word\": \" 함수적 종속성 \", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \" 한 속성의 값이 다른 속성의 값에 종속적인 관계를 갖는 특징을 말함 \\n - 즉, 어떤 속성 A의 값에 의해 다른 속성 B도 유일하게 결정된다면, B는 A에 함수적으로 종속됐다 하고, \\n이를 수식으로 나타내면 A → B라고 표현함 \\n\\n1) 완전 함수적 종속 \\n- 특정 컬럼이 기본키에 대해 완전히 종속될 때를 말함 \\n- PK를 구성하는 컬럼이 2개 이상일 경우 PK값 모두에 의한 종속관계를 나타낼 때 완전 함수 종속성 만족 \\nex) (주문번호 + 제품번호)에 의해 수량 컬럼의 값이 결정됨\\n\\n\\n2) 부분 함수적 종속 - 기본키 전체가 아니라, 기본키 일부에 대해 종속될 때를 말함 \\nex) 수강기록 테이블에서 학생번호와 과목이 PK라고 가정할 때, 과목에 의해서도 교수가 결정되면 부분 \\n함수적 종속성 관계!\", \"showDescription\": true}, {\"id\": \"1741925355202\", \"word\": \"속성의 분류\", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"1) 속성의 특성에 따른 분류 \\n① 기본 속성 - 업무로부터 추출된 모든 속성 - 엔터티에 가장 일반적으로 많이 존재하는 속성 \\nex) 원금, 예치기간 등 \\n② 설계 속성 - 기본 속성 외에 업무를 규칙화하기 위해 새로 만들어지거나 기본 속성을 변형하여 만들어지는 속성 \\nex) 상품코드, 지점코드, 예금분류 등 \\n③ 파생 속성 - 다른 속성에 의해 만들어지는 속성 - 일반적으로 계산된 값들이 해당 - 데이터 정합성을 유지하기 위해 가급적 적게 정의하는 것이 좋음 \\nex) 합계, 평균, 이자 등 \\n2) 엔터티 구성방식에 따른 분류 \\n① PK(Primary Key, 기본키)  - 인스턴스를 식별할 수 있는 속성 \\n② FK(Foreign Key, 외래키) 속성 - 다른 엔터티와의 관계에서 포함된 속성 \\n페이지 8 / 154 \\nHDATALAB \\n③ 일반 속성 - 엔터티에 포함되어 있고 PK/FK에 포함되지 않는 속성 \\n3) 분해 여부에 따른 속성 \\n① 단일 속성  - 하나의 의미로 구성된 경우 \\nex) 회원ID, 이름 등 \\n② 복합 속성  - 여러개의 의미로 구성된 경우 \\nex) 주소(시, 구, 동 등으로 분해 가능) 등  \\n③ 다중값 속성 - 속성에 여러 개의 값을 가질 수 있는 경우 - 다중값 속성은 엔터티로 분해 \\nex) 상품 리스트 등\", \"showDescription\": true}, {\"id\": \"1741925355953\", \"word\": \"속성의 명명규칙 \", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"① 해당 업무에서 사용하는 이름을 부여 \\n② 서술식 속성명은 사용하지 않음 \\n③ 약어의 사용은 가급적 제한 \\n④ 전체 데이터 모델에서 유일한 명칭 \", \"showDescription\": true}, {\"id\": \"1741925356678\", \"word\": \"도메인(Domain) \", \"images\": [], \"showWord\": true, \"showImages\": true, \"description\": \"- 도메인은 각 속성이 가질 수 있는 값의 범위를 의미함 \\n- 엔터티 내에서 속성에 대한 데이터 타입과 크기, 제약사항을 지정하는 것이다\", \"showDescription\": true}]}}]}]','\"데이터 모델링의 이해\"','\"속성\"',NULL,NULL),(4,NULL,NULL,NULL,'데이터 모델링의 이해','관계',NULL,NULL),(5,NULL,NULL,NULL,'데이터 모델링의 이해','식별자',NULL,NULL),(6,NULL,NULL,NULL,'데이터 모델링의 이해','정규화',NULL,NULL),(7,NULL,NULL,NULL,'데이터 모델링의 이해','관계와 조인의 이해',NULL,NULL),(8,NULL,NULL,NULL,'데이터 모델링의 이해','모델이 표현하는 트랜잭션의 이해',NULL,NULL),(9,NULL,NULL,NULL,'데이터 모델링의 이해','Null 속성의 이해',NULL,NULL),(10,NULL,NULL,NULL,'데이터 모델링의 이해','본질식별자 vs 인조식별자',NULL,NULL),(11,NULL,NULL,NULL,'SQL 기본 및 활용','관계형 데이터베이스 개요',NULL,NULL),(12,NULL,NULL,NULL,'SQL 기본 및 활용','SELECT 문',NULL,NULL)